/**
 * GV EXTENSION | Background script
 *
 * Sends data from the extension to the pusherman node application.
 * Also sends download data back to the content script to update the carbon meter.
 */

// Set configuration defaults (in SCREAMING_SNAKE)
chrome.runtime.onInstalled.addListener(() => {
    chrome.storage.local.set({
        PROJECT: 'hello-world',
        DOMAIN: 'hello.world',
        SITE_CODE: 'hw',
        SCRIPTS: ['hello', 'world'],
        STAGING: true,
        LOGIN_STATE: false,
        CONFIG_STATE: false,
        SVG_STATE: false,
        MD_STATE: false,
        DOWNLOAD_SIZE: 0,
    });
});

// PUBLISH MODAL ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

// Handle messages from the content script
chrome.runtime.onConnect.addListener((port) => {
    port.onMessage.addListener((request) => {
        // if (port.name === 'login') {
        //     console.log('port name login registered');
        //     let url = 'https://test.greenvision.media:5555/api/v1/hello';
        //     fetch(url, {
        //         method: 'POST',
        //         body: JSON.stringify({
        //             username: request.username,
        //             password: request.password,
        //         }),
        //     })
        //         .then(() => {
        //             port.postMessage({ response: 'pm-login' });
        //             port.disconnect();
        //         })
        //         .catch((e) => {
        //             console.log(e);
        //             port.disconnect();
        //         });
        // }
        if (port.name === 'site') {
            let url = 'https://pusher.free.beeceptor.com',
                formData = new FormData();
            // Append both the file and the configuration data
            formData.append('file', request.file);
            // formData.append('config', request.config);
            fetch(url, {
                method: 'POST',
                body: formData,
            })
                .then(() => {
                    port.postMessage({ response: 'pm-complete' });
                    port.disconnect();
                })
                .catch((e) => {
                    console.log(e);
                    port.disconnect();
                });
        }
    });
});

// CARBON METER ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

// Store download size for use by
chrome.downloads.onCreated.addListener((item) => {
    chrome.storage.local.set({
        DOWNLOAD_SIZE: item.fileSize,
    });
});
